        <script>
            $(document).ready(function() {
                $('#accountDeleteToggle').on('change', function() {
                    var status = $(this).is(':checked') ? 1 : 0;

                    var appMODE = "{{ env('APP_MODE') }}"
                    if(appMODE == 'DEMO'){
                        toastr.error('This Is Demo Version. You Can Not Change Anything');
                        return;
                    }

                    $.ajax({
                        url: "{{ route('seller.update-status') }}",
                        type: "POST",
                        data: {
                            _token: "{{ csrf_token() }}",
                            online_status: status
                        },
                        success: function(response) {
                            if(response.success) {
                                toastr.success('{{ __("translate.Status updated successfully to") }}' + (response.status ? 'Online' : 'Offline'), 'Success');
                            } else {
                                toastr.error('{{ __("translate.Failed to update status.") }} ', 'Error');
                            }
                        },
                        error: function(xhr) {
                            toastr.error('{{ __("translate.An error occurred while updating status.") }}', 'Error');
                        }
                    });
                });
            });
        </script>


